custom.scss:
// Custom Style
// Sticky Toolbar Mobile
// -----------------------------------------------------------------------------
.container {
    @media (max-width: 1024px) {
        padding-left: 24px;
        padding-right: 24px;
    }
}
.openSearchMobile {
    .navUser {
        z-index: 50;
    }
}

.halo-sticky-toolbar-mobile {
    background: #C9A241;
    border: 1px solid #C9A241;
}

.halo-sticky-toolbar-mobile span {
    color: #ffffff;
}

.halo-sticky-toolbar-mobile a {
    text-decoration: none;
}

.halo-sticky-toolbar-mobile a .icon {
    margin: 0 auto;
    width: 100%;
    height: 100%;
}

.halo-sticky-toolbar-mobile a .text {
    margin: 0 auto;
}

.halo-sticky-toolbar-mobile span.icon svg {
    fill: #ffffff;
}

.halo-sticky-toolbar-mobile {
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
    z-index: 99;
    box-shadow: 0 0 9px rgba(0, 0, 0, 0.1);
    transition: transform .6s;
}

.bottom-bar {
    padding: 7px 24px 6px;
    min-height: 64px;
}

.bottom-bar--list {
    list-style: none;
    padding: 0;
    display: flex;
    justify-content: space-between;
    margin: 0;
    align-items: center;
}

.bottom-bar--list .list-item {
    width: 20%;
    position: relative;
}

.bottom-bar--list li>a {
    padding: 7px 2px;
    display: block;
    pointer-events: auto;
    text-align: center;
}

.bottom-bar--list li>a[data-cart-sidebar] {
    position: relative;
}

.bottom-bar--list span {
    display: block;
    pointer-events: none;
    font-size: 10px;
    font-style: normal;
    font-weight: 500;
    text-transform: uppercase;
}

.bottom-bar--list .icon+.text {
    margin-top: 1px;
    line-height: 1;
}

.bottom-bar--list svg {
    width: 18px;
    height: 18px;
}

.bottom-bar--list .icon-cart svg {
    width: 20px;
    height: 20px;
}

.bottom-bar--list .icon.icon-collection {
    padding: 1px;
}

.bottom-bar--list .cart-count-bubble {
    position: absolute;
    top: 11px;
    right: 30px;
    color: #ffffff;
    min-width: 22px;
    min-height: 15px;
    text-align: center;
    border-radius: 50%;
    font-size: 12px;
    font-weight: 400;
    line-height: 22px;
}

.bottom-bar--list .cart-count-bubble span {
    color: #ffffff;
    font-size: 8px;
}

@media (max-width: 1024px) {
    .sticky-toolbar-mobile .footer {
        padding-bottom: 60px;
    }
}

@media (min-width: 1025px) {
    .halo-sticky-toolbar-mobile {
        display: none;
    }
}

/* Default For Custom Theme */
body.not-scroll {
    overflow-y: hidden;
}
main.body {
    overflow: hidden;
   

    &:has(.custom-ask-form),
    &:has(.member-benefic),
    &:has(.competition),
    &:has(.special-product-blocks){
        margin-bottom: 34px;
    }
    &:has(.terms-conditions-layout){
         @media (max-width: 767px) {
             margin-bottom: 32px;
        }
    }

    &:has(.reminder-top-banner){
        margin-bottom: 56px;
        @media (max-width: 900px) {
            margin-bottom: 25px;
        }
        @media (max-width: 767px) {
            margin-bottom: 0px;
        }
    }
    &#main-content:has(.custom-event-page){
        @media (max-width: 767px) {
            margin-bottom: -35px;
            .halo-subscription .halo-subsciption-content .subsciption-content-left {
                padding: 0 13px;
            }
        }
    }

    &#main-content:has(.section-condition--event){
        @media (max-width: 767px) {
            margin-bottom: 15px;
        }
    }

    &:has(.member-benefic) {
        @media (max-width: 767px) {
            margin-bottom: 5px;
        }
    }
    &:has(.competition) {
        margin-bottom: 103px;
        @media (max-width: 767px) {
            margin-bottom: 14px;
        }
    }

    .page-type-giftcertificates & {
        @media (max-width: 767px) {
            margin-bottom: 65px;
        }
    }

    &:has(.contact-top-info) {
        margin-top: 0;
        margin-bottom: 86px;

        @media (max-width: 767px) {
            margin-bottom: 32px;
        }
        .page-content {
            margin-top: 0;
        }
    }

    .page-type-page & {
        &:has(.alertBox--success){
            margin-bottom: 5px;
        }
    }
}

.flecha-m {
    font-family: "flecha-m";
}

.termina {
    font-family: 'Termina';
}

.fw-500 {
    font-weight: 500 !important;
}

.section-condition {
    margin-bottom: -5px;
    @media (max-width: 767px) {
        padding: 0 4px;
    }        
    h3 {
        @media (max-width: 767px) {
            text-align: center !important;
        }
    }

    .custom-page-contents {
        display: flex;
        margin-top: 32px;
    
        @media (max-width: 901px) {
            flex-direction: column;
        }

        @media (max-width: 767px) {
            margin-top: 12px;
        }

        .custom-page-text {
            text-align: center;
            font-size: 16px;
            line-height: normal;
            letter-spacing: 0.1px;
        }
    
        .item--left {
            width: 42.4%;
    
            @media (max-width: 901px) {
                width: 100%;
                text-align: center;
            }
    
            p {
                max-width: 416px;
                @media (min-width: 768px) {
                    text-align: left;
                }

                @media (max-width: 767px) {
                    max-width: 345px;
                    margin-left: auto;
                    margin-right: auto;
                }
            }
        }
    
        .item--right {
            width: 50%;
    
            @media (max-width: 901px) {
                width: 100%;
                text-align: center;
                margin-top: 18px;
            }

            @media (max-width: 767px) {
                margin-top: 5px;
            }

            .custom-page-text {
                @media (min-width: 768px) {
                    text-align: left;
                }
                @media (max-width: 767px) {
                    max-width: 340px;
                    margin-left: auto;
                    margin-right: auto;
                }
            }
    
            p {
                max-width: 392px;
            }
        }
    }
}

.custom-page-layout {
    .custom-page-heading-large {
        color: #252525;
        font-size: 64px;
        font-style: normal;
        font-size: clamp(40px, 5vw, 64px);
        font-family: 'flecha-m';
        font-weight: 400;
        line-height: normal;
        letter-spacing: -0.96px;
    }
    .custom-page-heading {
        margin-top: 0;
        color: #252525;
        font-size: clamp(40px, 5vw, 56px);
        font-family: 'flecha-m';
        font-style: normal;
        font-weight: 400;
        line-height: 49px;
        letter-spacing: -0.84px;
    }
    
    .custom-page-subHeading {
        color: #252525;
        font-size: 32px;
        font-size: clamp(20px, 3vw, 32px);
        font-family: 'flecha-m';
        font-style: normal;
        font-weight: 500;
        line-height: 40px;
        letter-spacing: 0.1px;
    
        @media (max-width: 767px) {
            font-family: 'flecha-s';
            font-style: normal;
            line-height: 23px;
            letter-spacing: 0.1px;
        }
    }
    
    .custom-page-heading-small {
        color: #252525;
        text-align: center;
        font-family: "flecha-m";
        font-size: clamp(32px, 5vw, 48px);
        font-style: normal;
        font-weight: 400;
        line-height: normal;
        letter-spacing: -0.72px;
    }
    
    .custom-page-title {
        color: #252525;
        font-size: clamp(20px, 3vw, 32px);
        font-style: normal;
        font-weight: 500;
        line-height: 40px; /* 125% */
        letter-spacing: 0.1px;
        font-family: "flecha-m";
    
        @media (max-width: 767px) {
            font-family: 'flecha-s';
            font-style: normal;
        }
    }
    
    .custom-page-text {
        color: #252525;
        font-size: clamp(12px, 1.5vw, 16px);
        font-style: normal;
        font-weight: 400;
        line-height: normal;
        letter-spacing: 0.1px;
    }

    .custom-page-text-small {
        color: #252525;
        font-family: $body-font-family;
        font-size: 12px;
        font-style: normal;
        font-weight: 400;
        line-height: 18px; /* 150% */
        letter-spacing: 0.1px;
    }
}

.btn-mobile {
    @media (max-width: 767px) {
        font-size: 8px;
        display: flex;
        width: 148px;
        height: 40px;
        padding: 0px 36px;
        justify-content: center;
        align-items: center;
        gap: 8px;
        flex-shrink: 0;
    }
}

/* ------------------------------ MemBer Page------------------------------------ */

.halo-page-content.page-content{
    @media (max-width: 767px) {
        margin-top: 0;
    }
}
/* Member Introduce Page */
.member-introduce {
    .container {
        @media (min-width: 1300px) {
            max-width: 1470px;
            width: 100%;
            margin-left: auto;
            margin-right: auto;
            padding-right: 0;
        }

        @media (max-width: 767px) {
            padding-top: 12px;
        }
    }
    .introduce__content {
        display: flex;
        width: 100%;
        justify-content: space-between;
        padding-top: 3px;
        

        @media (min-width: 1300px) {
            padding-left: 92px;
            padding-right: 0px;
        }

        @media (max-width: 767px) {
            flex-direction: column;
            align-items: center;
            padding-left: 14px;
            padding-right: 14px;
        }

        .introduct__text {
            width: 100%;
            max-width: 416px;
            margin-right: 106px;

            @media (max-width: 1600px) {
                margin-right: 50px;
            }

            @media (max-width: 767px) {
                margin-right: 0;
                max-width: 100%;
                margin-bottom: 64px;
            }

            .desc {
                margin-top: 66px;
                max-width: 375px;

                @media (max-width: 767px) {
                    margin-bottom: 0px;
                    margin-top: 20px;
                    max-width: 305px;
                }
            }

            .buttons-wrapper {
                margin-top: 69px;

                @media (max-width: 1024px) {
                    justify-content: flex-start !important;
                    margin-top: 32px;
                }

                .button {
                    @media (min-width: 1024px) {
                        min-width: 178px;
                    }

                    +.button {
                        margin-left: 28px;

                        @media (max-width: 767px) {
                            margin-left: 36px;
                        }
                    }
                }

                @media (max-width: 767px) {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }
            }
        }
    }
}

/* Member Benefic Page */
.member-benefic {
    margin-top: 164px;

    @media (max-width: 767px) {
        margin-top: 60px;
    }
    .benefic-content {
        .benefic__list {
            --f-col: 3;
            --f-gap: 0px;
            display: flex;
            flex-wrap: wrap;
            margin-left: calc(var(--f-gap) / -2);
            margin-right: calc(var(--f-gap) / -2);
            align-items: center;
            justify-content: center;
            list-style-type: none;
            margin-top: 67px;
            padding-left: 14px;
            padding-right: 12px;

            @media (max-width: 767px) {
                --f-col: 1;
                width: 100%;
                max-width: 350px;
                margin-left: auto;
                margin-right: auto;
                margin-top: 24px;
                padding-left: 5px;
                padding-right: 5px;
            }

            .benefic__item {
                padding-left: calc(var(--f-gap) / 2);
                padding-right: calc(var(--f-gap) / 2);
                flex: 0 0 calc(100% / var(--f-col));
                max-width: calc(100% / var(--f-col));
                margin-bottom: 53px;
                display: flex;
                justify-content: center;
                align-items: center;
                flex-direction: column;
                text-align: center;

                @media (max-width: 767px) {
                    flex-direction: row;
                    text-align: left;
                    justify-content: flex-start;
                    margin-bottom: 36px;
                }

                .benefic__icon {
                    @media (max-width: 767px) {
                        width: 100%;
                        max-width: 66px;
                        margin-right: 25px;
                        transform: translateY(10px);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        transform: translateY(4px);
                    }

                    &.hide-on-mobile {
                        @media (max-width: 767px) {
                            display: none;
                        }
                    }

                    &.hide-on-desktop {
                        @media (min-width: 768px) {
                            display: none;
                        }
                    }

                    svg {
                        @media (max-width: 767px) {
                            max-width: 100%;
                            max-height: 100%;
                        }
                    }
                }

                &:nth-child(2) {
                    @media (min-width: 1024px) {
                        padding-right: 25px;
                    }
                }

                &:nth-child(2n) {
                    @media (min-width: 1024px) {   
                        padding-left: 15px;
                    }
                }

                .benefic__text {
                    .title {
                        margin-bottom: 4px;

                        @media (max-width: 767px) {
                            margin-top: 0px;
                            margin-bottom: 0px;
                        }
                    }

                    .desc {
                        max-width: 198px;
                        @media (max-width: 767px) {
                            margin-bottom: 0px;
                            max-width: 205px;
                        }
                    }
                }
                
            }
        }
       
    }
}

/* Member Convince */
.member-convince {
    // margin-top: 95px;
    margin-top: 70px;

    @media (max-width: 767px) {
        margin-top: 0;
    }

    .convince-button {
        display: flex;
        justify-content: center;
        align-items: center;
        margin-top: 28px;

        @media (max-width: 767px) {
            margin-top: 20px;
        }

        .button {
            @media (min-width: 1024px) {
                min-width: 178px;
            }
        }
    }
}

/* Member How It Work */
.how-it-work {
    margin-top: 164px;

    @media (max-width: 767px) {
        margin-top: 60px;
    }

    .how-it-work-content {
        .how-it-work__list {
            display: flex;
            justify-content: center;
            align-items: center;
            margin-top: 79px;
            margin-left: -14px;

            @media (max-width: 767px) {
                margin-left: 0;
                margin-top: 37px;
            }

            .how-it-work__item {
                .how-it-work__title {
                    display: flex;
                    width: 62px;
                    height: 21px;
                    flex-direction: column;
                    justify-content: center;
                    flex-shrink: 0;
                    color: #252525;
                    font-family: 'flecha-s';
                    font-size: 20px;
                    font-style: normal;
                    font-weight: 500;
                    line-height: normal;
                    letter-spacing: 0.1px;
                    border-bottom: 1px solid transparent;
                    padding-bottom: 20px;
                    
                    &.is-active {
                        border-color: #252525;
                    }
                }

                + .how-it-work__item {
                    margin-left: 94px;

                    @media (max-width: 767px ) {
                        margin-left: 77px;
                    }

                    @media (max-width: 380px) {
                        margin-left: 50px;
                    }
                }
            }
        }
        
        .how-it-work__contents {
            position: relative;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #F7F3E8;

            &::before {
                content: "";
                position: absolute;
                background-color: inherit;
                left: -100%;
                top: 0;
                width: 100%;
                height: 100%;
            }

            &::after {
                content: "";
                position: absolute;
                background-color: inherit;
                right: -100%;
                top: 0;
                width: 100%;
                height: 100%;
            }

            .how-it-work__content-wrapper {
                .how-it-work__content {
                    display: none;
                    padding-top: 75px;
                    padding-bottom: 65px;
                    max-width: 390px;

                    @media (max-width: 767px) {
                        max-width: 340px;
                        padding-top: 56px;
                        padding-bottom: 55px;
                    }

                    .custom-page-title {
                        @media (max-width: 767px) {   
                            margin-bottom: 0;
                        }
                    }

                    .custom-page-text {
                        @media (max-width: 767px) {   
                            margin-bottom: 10px;
                        }
                    }

                    &.is-active {
                        display: block;
                    }
                    
                    h4 {
                        margin: 0;
                        margin-bottom: 7px;
                    }
                }
            }
        }
    }
}

/* FAQs Layout */
.custom-faqs {
    margin-top: 163px;
    margin-bottom: 15px;

    @media (max-width: 767px) {
        margin-top: 62px;
    }
    .faqs-content {
        width: 100%;
        max-width: 727px;
        margin-left: auto;
        margin-right: auto;

        .faq-title {
            margin-bottom: 26px;
            margin-top: 0;
        }
        .halo-faqs-content {
            .card {
                border-bottom: none;
                border-bottom: 1px solid #F7F3E8;

                &:nth-child(1) {
                    border-top: 1px solid #F7F3E8;

                }
                .card-header {
                    .title {
                        position: relative;
                        padding-top: 14px;
                        padding-bottom: 5px;

                        @media (max-width: 767px) {
                            padding-left: 15px;
                            padding-right: 50px;
                            padding-top: 16px;
                            padding-bottom: 13px;
                        }
                        &::before {
                            width: 0;
                        }

                        &::after {
                            content: "";
                            position: absolute;
                            top: 50%;
                            right: 0;
                            transform: translateY(-50%);
                            background-image: url('data:image/svg+xml, <svg xmlns="http://www.w3.org/2000/svg" width="17" height="8" viewBox="0 0 17 8" fill="none"><path d="M16 1L8.5 7L1 1" stroke="%23252525" stroke-width="1.25" stroke-linejoin="bevel"/></svg>');
                            background-repeat: no-repeat;
                            width: 17px;
                            height: 10px;
                            transition: transform 0.5s ease;

                            @media (max-width:767px) {
                                right: 14px;
                            }
                        }

                        &.collapsed {
                            @media (min-width: 768px) {
                                padding-left: 0;
                            }

                            &::after {
                                transform: translateY(-50%) rotate(180deg);
                            }
                        }
                        span {
                            background-image: none;
                        }

                        .custom-page-text {
                            @media (max-width: 767px) {
                                display: block;
                                line-height: 18px;                               
                            }
                        }
                    }
                    
                }

                .card-body {
                    @media (max-width: 767px) {
                        padding-left: 25px;
                        padding-right: 25px;
                    }
                }
            }
        }
    }
}

/* ------------------------------ Competition Page------------------------------------ */
.competition {
    margin-bottom: 12px;

    // @media (max-width: 767px) {
    //     margin-bottom: -88px;
    // }

    .container {
        @media (max-width: 767px) {
            
        }
    }

    .competition-list-wrapper + .competition-list-wrapper {
        padding-top: 12px;

        @media (max-width: 767px) {
            padding-top: 8px;
        }
    }

    .custom-page-heading {
        margin-bottom: 65px;
        
        @media (max-width: 767px) {
            margin-top: 15px;
            margin-bottom: 47px;
        }
    }

    .custom-page-subHeading {
        max-width: 600px;
        margin-left: auto;
        margin-right: auto;

        @media (max-width: 767px) {
            text-align: center;
            max-width: 342px;
            line-height: 25px;
        }
    }

    .competition__list {
        --f-col: 3;
        --f-gap: 24px;
        display: flex;
        flex-wrap: wrap;
        margin-left: calc(var(--f-gap) / -2);
        margin-right: calc(var(--f-gap) / -2);
        margin-top: 69px;

        @media (max-width: 767px) {
            margin-top: 55px;
            margin-left: 0;
            margin-right: 0;
            margin-bottom: 21px;
        }

        + .competition__list {
            margin-top: 78px;
        }

        .slick-dots {
            display: none !important;
        }
        .slick-list {
            @media (max-width: 1024px) {
                padding-right: 93px;
                margin-right: -15px;
            }

            @media (max-width: 767px) {
                padding-right: 110px;
            }
        }

        .competition__item {
            padding-left: calc(var(--f-gap) / 2);
            padding-right: calc(var(--f-gap) / 2);
            flex: 0 0 calc(100% / var(--f-col));
            max-width: calc(100% / var(--f-col));

            .item__img {
                @media (max-width: 767px) {
                    max-height: 288px;
                    overflow: hidden;
                }

                img {
                    width: 100%;
                    height: 100%;
                    object-fit: fill;
                }
            }

            .item__info {
                max-width: 320px;
                
                .custom-page-title {
                    margin-top: 35px;
                    margin-bottom: 30px;

                    @media (max-width: 767px) {
                        margin-top: 15px;
                        margin-bottom: 7px;
                    }
                }

                .date {
                    margin-bottom: 13px;
                    font-weight: 500;

                    @media (max-width: 767px) {
                        
                    }
                }

                .custom-page-text:not(.date) {
                    margin-bottom: 14px;

                    @media (max-width: 767px) {
                        max-width: 205px;
                        margin-bottom: 26px;
                    }
                }

                .button {
                    @media (min-width: 768px) {
                        min-width: 178px;
                    }
                }
            }
         }

    }
    
    .progress {
        @media (min-width: 1025px) {
            display: none;
        }

        @media (max-width: 767px) {
            width: calc(100% - 30px);
        }

        .bar-thumb {
            @media (max-width: 767px) {
                border-top-right-radius: 12px;
                border-bottom-right-radius: 12px;
            }
        }
    }
}

/* Competition Post */
.competition-post {
    padding-top: 3px;

    @media (max-width: 767px) {
        padding-top: 15px;
    }

    h3 {
        margin-bottom: 25px;
        
        @media (max-width: 767px) {
            text-align: center;
            margin-bottom: 10px;
        }
    }

    h3 ~ p {
        @media (max-width: 767px) {
            text-align: center;
        }
    }

    .custom-page-subHeading {
        margin-bottom: 30px;

