/* Change Menu Icon  */
:root {
    --animation-curve: cubic-bezier(1, 0, 0, 1);
}


.mobileMenu-toggle {
    .mobileMenu-toggleIcon {
        transition: all 0.6s var(--animation-curve);

        &::before,
        &::after {
            transition: all 0.6s var(--animation-curve);
        }
    
        &::before {
            left: 0;
            transform: rotateZ(0deg) !important;
            transform-origin: left top !important;
        }
    
        &::after {
            left: 0%;
            transform: rotateZ(0deg) !important;
            transform-origin: left bottom !important;
        }
    }

    &.is-open {
        .mobileMenu-toggleIcon {
            &::before {
                transform: rotateZ(43deg) !important;
                left: 10% !important;
            }
            &::after {
                transform: rotateZ(-43deg) !important;
                left: 10% !important;
            }
            @media (max-width: 1024px) {
                &::after {
                    transform: rotateZ(-35deg) !important;
                    left: 0 !important;
                    width: 30px;
                }
                 &::before {
                    transform: rotateZ(35deg) !important;
                    left: 0px !important;
                    width: 30px;
                }
            }
        }
    }
}


