/* Styles for the overlay */
.digital-overlay {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.7);
    z-index: 1000;
}

/* Styles for the popup container */
.digital-popup {
    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.3);
    z-index: 1001;
    width: 80%;
    height: 80%;
    max-width: 800px;
    max-height: 800px;
}

  /* Style for the close button */
.digital-popup .close-btn {
    position: absolute;
    top: 10px;
    right: 10px;
    cursor: pointer;
    font-size: 36px;
}

  /* Style for the zoomed image */
.digital-popup img {
    width: 100%;
    height: 100%;
    object-fit: contain;
}


