.event-title {
    color: #252525;
    font-family: "flecha-m";
    font-size: 32px;
    font-style: normal;
    font-weight: 500;
    line-height: normal;
    letter-spacing: 0.1px;

    .event-block__left & {
        position: relative;
        padding-bottom: 12px;

        &::after {
            content: "";
            position: absolute;
            bottom: 0;
            left: 0;
            display: block;
            background: #F7F3E8;
            width: 100%;
            max-width: 208px;
            height: 1.25px;
        }
    }
}

.custom-event-page {
    margin-bottom: 79px;
    .custom-event-block {
        padding-top: 162px;
        display: flex;
        @media (min-width: 1025px) {
            margin-left: -52px;
            margin-right: -52px;
        }

        @media (max-width: 1024px) {
            padding-top: 68px;
        }

        .event-block__left {
            padding-left: 52px;
            padding-left: 52px;
            width: 100%;
            max-width: 260px;
            -ms-overflow-style: none;
            scrollbar-width: none;

            &::-webkit-scrollbar {
             display: none;
            }

            @media (max-width: 1024px) {
                position: fixed;
                background-color: #ffffff;
                left: -100%;
                top: 80px;
                bottom: 0;
                width: 100%;
                max-width: unset;
                z-index: 999;
                padding-left: 24px;
                padding-right: 147px;
                overflow: auto;
                transition: all linear 0.3s;

                &.is-open {
                    left: 0;
                    top: 80px;
                    overflow: auto;
                }

                &::-webkit-scrollbar {
                    width: 8px;
                }
                
                /* Track */
                &::-webkit-scrollbar-track {
                    background: #f1f1f1;
                }
                
                /* Handle */
                &::-webkit-scrollbar-thumb {
                    background: #000000;
                }
            }

            .filter-sidebar-heading {
                display: none;

                @media (max-width: 1024px) {
                    display: block;
                    color: #252525;
                    font-family: "flecha-m";
                    font-size: 40px;
                    font-style: normal;
                    font-weight: 400;
                    line-height: normal;
                    letter-spacing: -0.6px;
                    margin-bottom: 65px;
                    margin-top: 36px;
                }
            }

            .icon-close-filter {
                display: none;

                @media (max-width: 1024px) {
                    position: absolute;
                    right: 24px;
                    top: 36px;
                    background-color: #ffffff;
                    padding-left: 12px;
                    display: inline-block;
                    
                    .icon {
                        width: 16px;
                        height: 16px;
                    }
                }
            }

            .filter-block {
                .group-wrapper {
                    max-height: 160px;
                    overflow: auto;
                    margin-left: 10px;
                    &::-webkit-scrollbar {
                        width: 8px;
                    }
                    
                    /* Track */
                    &::-webkit-scrollbar-track {
                        background: #F7F3E8;
                        border-radius: 50px;
                    }
                    
                    /* Handle */
                    &::-webkit-scrollbar-thumb {
                        border-radius: 50px;
                        background: #252525;
                    }
                    .check-box-group {
                        margin-bottom: 6px;
                    }
                }
                
                & + .filter-block {
                    margin-top: 26px;
                }

                .filter-heading {
                    margin-bottom: 10px;
                    margin-top: 0;
                }

                .check-box-group {
                    .filter-check {
                        display: none;

                        & + .filter-label::before {
                            content: "";
                            display: inline-block;
                            width: 15px;
                            height: 15px;
                            background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="17" height="17" viewBox="0 0 17 17" fill="none"><path d="M8.47852 15.7898C12.6207 15.7898 15.9785 12.4121 15.9785 8.24547C15.9785 4.07887 12.6207 0.701172 8.47852 0.701172C4.33638 0.701172 0.978516 4.07887 0.978516 8.24547C0.978516 12.4121 4.33638 15.7898 8.47852 15.7898Z" stroke="%23252525" stroke-width="1.25" stroke-miterlimit="10"/></svg>');
                            background-size: cover;
                            margin-right: 8px;
                        }

                        &:checked {
                            & +.filter-label::before {
                                border-radius: 50%;
                                background-color: #252525;
                            }
                        }
                    }

                    .filter-label {
                        color: #252525;
                        font-size: 12px;
                        font-style: normal;
                        font-weight: 400;
                        line-height: 18px;
                        letter-spacing: 0.1px;
                    }
                }
            }

            .filter-action {
                margin-top: 34px;
                display: flex;
                flex-direction: column;

                @media (max-width: 1024px) {
                    margin-bottom: 36px;
                }

                .button-fillter {
                    width: 148px;
                    height: 40px;
                    line-height: 40px;
                    font-size: 8px;
                }

                .filter-clear {
                    margin-top: 36px;
                    color: #252525;
                    font-size: 12px;
                    font-style: normal;
                    font-weight: 500;
                    line-height: 18px;
                    letter-spacing: 0.1px;
                    text-decoration-line: underline;
                }
            }
        }

        .event-block__right {
            padding-left: 103px;

            @media (max-width: 1024px) {
                padding-left: 15px;
                padding-right: 15px;
                width: 100%;
            }

            .button-event-filter {
                display: none;

                @media (max-width: 1024px) {
                    display: block;
                    width: 100%;
                    max-width: 148px;
                    background: #F7F3E8;
                    margin-bottom: 37px;
                    margin-left: auto;
                    margin-right: auto;
                    font-size: 8px;
                    letter-spacing: 1.6px;
                    border: transparent;
                    line-height: 44px;

                    &:hover {
                        background-color: #ffffff;
                        color: #252525;
                        border:1px solid #C9A241;
                    }

                    .icon--event-filter {
                        margin-right: 4px;
                    }
                }
            }

            .event-content {
                display: none;
                position: relative;
               
                & + .event-content {
                    margin-top: 166px;
                    &:after {
                        content: "";
                        position: absolute;
                        top: -10%;
                        height: 1.25px;
                        width: 100%;
                        background: #F7F3E8;
                        @media (max-width: 767px) {
                            top: -3%;
                        }
                    }
                    @media (max-width: 1024px) {
                        margin-top: 150px;
                    }
                    @media (max-width: 767px) {
                        margin-top: 37px;
                    }
                }
                .event-content__heading {
                    color: #252525;
                    font-size: 53px;
                    font-style: normal;
                    font-weight: 500;
                    line-height: 40px;
                    font-family: "flecha-m";
                    letter-spacing: 0.1px;
                    margin-bottom: 75px;
                    margin-top: 4px;

                    @media (max-width: 767px) {
                        font-size: 40px;
                        text-align: center;
                        letter-spacing: -0.6px;
                        font-weight: 400;
                        margin-bottom: 50px;
                        margin-top: 0px;
                    }
                }

                .event-content__list {
                    .event-content__item {
                        display: flex;
                        @media (max-width: 767px) {
                            flex-direction: column;
                            .item__image {
                                width: 251px;
                                height: 251px;
                                margin-bottom: 14px;
                                img {
                                    width: 100%;
                                    height: 100%;
                                    object-fit: cover;
                                }
                            }
                        }

                        & + .event-content__item {
                            margin-top: 80px;
                            @media (max-width: 767px) {
                                margin-top: 35px;
                            }

                        }

                        .item__image {

                        }

                        .item__info {
                            display: flex;
                            flex-direction: column;
                            margin-left: 24px;

                            @media (max-width: 767px) {
                                margin-left: 0;
                            }
                            .item__heading {
                                color: #252525;
                                font-size: 32px;
                                font-family: "flecha-m";
                                font-style: normal;
                                font-weight: 500;
                                line-height: 40px;
                                letter-spacing: 0.1px;
                                margin: 0;
                                margin-bottom: 1px;
                                transform: translateY(-5px);

                                @media (max-width: 767px) {
                                    font-family: 'flecha-s';
                                    font-size: 20px;
                                    margin-bottom: 0;
                                }
                            }

                            p {
                                color: #252525;
                                font-size: 16px;
                                font-style: normal;
                                font-weight: 500;
                                line-height: normal;
                                letter-spacing: 0.1px;
                                margin-bottom: 0;

                                @media (max-width: 767px) {
                                    font-size: 12px;
                                }
                            }

                            span {
                                color: #252525;
                                font-size: 16px;
                                font-style: normal;
                                font-weight: 400;
                                line-height: normal;
                                letter-spacing: 0.1px;

                                @media (max-width: 767px) {
                                    font-size: 12px;
                                }
                            }

                            .item__author {
                                text-decoration-line: underline;
                                margin-bottom: 15px;
                            }

                            .item__author-book {
                                margin-bottom: 36px;
                                display: inline-block;

                                @media (max-width: 767px) {
                                    margin-bottom: 28px;
                                }
                            }

                            .italic {
                                font-style: italic;
                            }

                            .item__button {
                                margin-top: 40px;
                                font-size: 8px;
                                font-style: normal;
                                font-weight: 600;
                                line-height: normal;
                                letter-spacing: 1.6px;
                                display: flex;
                                width: 148px;
                                height: 40px;
                                justify-content: center;
                                align-items: center;

                                @media (max-width: 767px) {
                                    margin-top: 32px;
                                }
                            }
                            @media (max-width: 767px) {
                                margin-left: 0;
                                .item__author {
                                    margin-bottom: 3px;
                                    transform: translateY(-7px);
                                }
                            }
                        }
                    }
                }
                @media (max-width: 767px) {
                    &:not(:first-child) {
                        margin-top: 65px;
                        .event-content__heading {
                            display: none;
                        }
                    }
                }
            }

            .event-view-more-button {
                margin-top: 80px;
                padding-left: 32px;
                padding-right: 32px;

                @media (max-width: 767px) {
                    font-size: 8px;
                    min-width: 148px;
                    letter-spacing: 1.6px;
                    margin-top: 61px;
                }
            }
        }
    }
}

.icon-close-filter {
    display: none;

    @media (max-width: 1024px) {
        width: 22px;
        height: 22px;
        font-size: 0;
        overflow: hidden;
        overflow-x: hidden;
        overflow-y: hidden;
        position: relative;
        display: block;
        z-index: 1;
        outline: none;
        -webkit-box-shadow: none;
        box-shadow: none;
        margin-bottom: 5px;
        .icon {
            width: 16px;
            height: 16px;
        }
    }
}

// Begin Page Reminder 
.reminder-top-banner {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    flex-wrap: wrap;
    position: relative;
    @media (min-width: 1255px) {
        min-height: 520px;
    }
    @media (max-width: 900px) {
        padding: 0 40px;
    }
    @media (max-width: 767px) {
        padding: 0 24px;
    }
    &__content {
        min-width: 392px;
        max-width: 445px;
        width: 50%;
        @media (max-width: 767px) {
            min-width: unset;
            width: 100%;
        }
        .title {
            color: #252525;
            font-size: clamp(40px, 5vw, 56px);
            font-style: normal;
            font-weight: 400;
            line-height: 1.1;
            letter-spacing: -0.84px;
            margin-top: 0;
            margin-bottom: 78px;
            @media (max-width: 930px) {
                margin-bottom: 25px;
            }
            @media (max-width: 767px) {
                padding-right: 30px;
                margin-bottom: 33px;
            }
        }
        .actions {
            .button {
                padding-left: 34px;
                padding-right: 34px;
                @media (max-width: 930px) {
                    padding-left: 30px;
                    padding-right: 30px;
                }
                @media (max-width: 767px) {
                    padding-left: 34px;
                    padding-right: 34px;
                    font-size: 8px;
                }
            }
            .button-register {
                margin-right: 20px;
                @media (max-width: 767px) {
                    margin-right: 30px;
                }
            }
        }
    }
    &__image {
        position: absolute;
        right: calc(100% - 1360px);
        top: 0;
        min-width: 304px;
        @media (max-width: 1440px) {
            right: calc(100% - 1350px);
        }
        @media (max-width: 1254px) {
            position: unset;
            right: unset;
            top: unset;
            flex: 1;
            margin-left: 40px;
        }
        @media (max-width: 767px) {
            margin-left: 0;
            width: 100%;
            margin-top: 62px
        }
    }

    .reminder-top-banner__content {
        .actions {
            .button {
                @media (min-width: 1025px) {
                    width: 178px;
                }
            }
        }
    }
}
.reminder-body {
    padding: 0 40px;
    margin-top: 157px;
    @media (max-width: 900px) {
        margin-top: 95px;
    }
    @media (max-width: 767px) {
        margin-top: 65px;
        padding: 0 24px;
    }
    .reminder-body-infor { 
        .title {
            font-family: "flecha-m";
            font-size: clamp(32px, 5vw , 56px);
            letter-spacing: -0.84px;
            margin-bottom: 33px;
        }
        .sub-title {
            font-size: clamp(16px, 2vw , 20px);
            letter-spacing: 0.1px;
            max-width: 600px;
            margin: 0 auto;
            padding: 0 50px;
        }
        .title,
        .sub-title {
            color: #252525;
            text-align: center;
            font-style: normal;
            line-height: normal;
            font-weight: 400;
            @media (max-width: 767px) {
                text-align: left;
                padding: 0;
                margin: 0;
                margin-bottom: 13px;
            }
        }
        .infor-blocks {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-top: 74px;
            margin-left: 108px; 
            margin-right: 108px;
            @media (max-width: 1024px) {
                margin-left: 0;
                margin-right: 0;
            }
            @media (max-width: 767px) {
                flex-direction: column;
                margin-top: 63px;
            }
            .infor-blocks-item {
                display: flex;
                width: 440px;
                height: 440px;
                padding: 21.6px 30px;
                justify-content: center;
                align-items: center;
                flex-direction: column;
                background-color: #B3D3E6;
                border-radius: 50px;
                color: #FFF;
                text-align: center;
                padding-top: 60px;
                &:nth-child(2) {
                    background-color: #1D4C5D;
                }
                @media (max-width: 1254px) {
                    width: 45%;
                }
                @media (max-width: 1024px) {
                    padding: 21.6px 0;
                }
                @media (max-width: 767px) {
                    justify-content: flex-start;
                    width: 100%;
                    height: fit-content;
                    padding-bottom: 75px;
                    max-width: 342px;
                    margin-bottom: 15px;
                    &:nth-child(2) {
                        margin-top: 20px;
                    }
                }

                .infor-blocks-item-title {
                    font-family: "flecha-m";
                    letter-spacing: -0.72px;
                    font-size: clamp(32px, 4.5vw , 48px);
                    color: #FFF;
                    margin-top: 0;
                    @media (max-width: 767px) {
                        margin-top: 45px;
                    }
                }
                .infor-blocks-item-sub-title {
                    color: #FFF;
                    font-family: "flecha-m";
                    font-size: clamp(20px, 2.5vw , 32px);
                    font-weight: 500;
                    line-height: 40px;
                    letter-spacing: 0.1px;
                    @media (max-width: 767px) {
                        margin-bottom: 6px;
                        margin-top: 8px;
                    }
                }
                .infor-blocks-item-description {
                    text-align: center;
                    font-size: clamp(16px, 1.5vw , 20px);
                    font-weight: 400;
                    letter-spacing: 0.1px;
                    padding: 0 48px;
                    transform: translateY(13px);
                     @media (max-width: 767px) {
                        padding: 0 35px;
                        font-size: 16px;
                        font-weight: 400;
                        line-height: normal;
                        letter-spacing: 0.1px;
                    }
                }
            }
        }
    }
}
.reminder-form {
    display: flex;
    justify-content: flex-start;
    align-items: flex-start;
    margin-top: 166px;
    padding: 0 110px;
    @media (max-width: 1024px) {
        padding: 0 40px;
    }
    @media (max-width: 900px) {
        margin-top: 130px;
    }
    @media (max-width: 767px) {
        flex-direction: column;
        margin-top: 16px;
        padding: 0 24px;
    }
