            }
            .form-actions {
                .button {
                    font-size: 12px;
                    max-width: 176px !important;
                    height: 48px;
                    @media (max-width: 767px) {
                        font-size: 8px;
                        max-width: 148px !important;
                        height: 40px;
                        margin-top: -15px;
                    }
                }
            }
            .reminder-policy {
                color: #252525;
                font-size: 10px;
                font-style: normal;
                font-weight: 400;
                line-height: normal;
                letter-spacing: 0.01px;
                max-width: 333px;
                margin-top: 15px;
            }
        }
        .check-box-group {
            .filter-check {
                display: none;

                & + .filter-label::before {
                    content: "";
                    display: inline-block;
                    width: 15px;
                    height: 15px;
                    background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="17" height="17" viewBox="0 0 17 17" fill="none"><path d="M8.47852 15.7898C12.6207 15.7898 15.9785 12.4121 15.9785 8.24547C15.9785 4.07887 12.6207 0.701172 8.47852 0.701172C4.33638 0.701172 0.978516 4.07887 0.978516 8.24547C0.978516 12.4121 4.33638 15.7898 8.47852 15.7898Z" stroke="%23252525" stroke-width="1.25" stroke-miterlimit="10"/></svg>');
                    background-size: cover;
                    margin-right: 8px;
                }

                &:checked {
                    & +.filter-label::before {
                        border-radius: 50%;
                        background-color: #252525;
                    }
                }
            }

            .filter-label {
                color: #252525;
                font-size: 12px;
                font-style: normal;
                font-weight: 400;
                line-height: 18px;
                letter-spacing: 0.1px;
            }
        }
        .custom-page-heading {
            color: #252525;
            font-family: "flecha-m";
            font-size: clamp(32px, 3vw, 48px);
            font-style: normal;
            font-weight: 400;
            line-height: normal;
            letter-spacing: -0.72px;
            padding-left: 27px;
            @media (max-width: 767px) {
                padding-left: 0px;
            }
        }
    }
}
#reminder-annouce-results {
    margin-bottom: 8px;
    @media (max-width: 767px) {
        padding: 0 24px;
    }
}
.reminder-annouce-wrapper {
    text-align: center;
    color: #252525;
    font-style: normal;
    width: 100%;
    max-width: 440px;
    margin: 0 auto;
    margin-top: 138px;
    .reminder-annouce-title {
        font-family: "flecha-m";
        font-size: 56px;
        font-weight: 400;
        line-height: 100px;
        letter-spacing: -0.84px;
        margin-bottom: 0px;
    }
    .reminder-annouce-content {
        text-align: center;
        font-size: 16px;
        font-weight: 500;
        line-height: 18px;
        letter-spacing: 0.1px;
        margin-bottom: 28px;

    }
    .reminder-annouce-button {
        padding-left: 44px;
        padding-right: 44px;    
        margin-right: 4px;
    }
}

/* Event Detail Page */
.event-detail__top {
    .container {
        @media (max-width: 767px) {
            padding-left: 24px;
            padding-right: 24px;
        }
    }

    .event-detail-top__wrapper {
        display: flex;

        @media (max-width: 800px) {
            flex-wrap: wrap;
        }

        .event-detail__top--left {
            .event-detail__img {
                width: 100%;
                max-width: 520px;

                @media (max-width: 1024px) {
                    max-width: 100%;
                }

                @media (max-width: 501px) {
                    max-width: 288px;
                }
            }
        }

        .event-detail__top--right {
            width: 100%;
            max-width: 652px;
            margin-left: 52px;

            @media (max-width: 1024px) {
                margin-left: 24px;
                max-width: 50%;
            }

            @media (max-width: 800px) {
                margin-left: 0;
                max-width: 100%;
            }

            @media (max-width: 501px) {
                max-width: 342px;
            }

            .event-detail__content {
                @media (max-width: 767px) {
                    padding-top: 36px;
                }
                h3 {
                    color: #252525;
                    font-size: 32px;
                    font-style: normal;
                    font-weight: 500;
                    line-height: 40px;
                    margin-bottom: 2px;
                    letter-spacing: 0.1px;
                    margin-top: 0;
                    transform: translateY(-4px);

                    @media (max-width: 767px) {
                        color: #252525;
                        font-family: "flecha-s";
                        font-size: 20px;
                        font-style: normal;
                        font-weight: 500;
                        line-height: 40px; /* 200% */
                        letter-spacing: 0.1px;
                    }
                }

                p {
                    display: flex;
                    flex-direction: column;
                    margin-bottom: 33px;

                    @media (max-width: 767px) {
                        margin-bottom: 25px;
                    }

                    &.author {
                        margin-bottom: 37px;

