                    letter-spacing: -0.96px;
                    margin-bottom: 13%;
                    @media (max-width: 768px) {
                        font-size: 32px;
                        margin-bottom: 8px;
                    }
                }
                .description {
                    font-size: 20px;
                    font-weight: 400;
                    line-height: normal;
                    font-style: normal;
                    letter-spacing: 0.1px;
                    max-width: 110px;
                    margin: 0 auto;

                    @media (max-width: 768px) {
                        font-size: 16px;
                        max-width: 90px;
                    }
                    min-height: 48px;
                }

                &:nth-child(2) {
                    .description {
                        max-width: 184px;
                    }
                }
            }
        }
    }
}

.custom-section-top-banner {
    &.banner-careers-top {
        background-color: #F3B47C;
        .banner-text.text-title {
            max-width: 856px;
        }
    }
    &.banner-careers-second {
        background-color: #F7F3E8;

        .digital-banner-text {
            @media (min-width: 1025px) {
                padding-top: 109px;
                padding-bottom: 139px;
            }
        }
        .banner-text {
            max-width: 600px;
            color: #C9A241;
            font-size: 20px;
            font-style: normal;
            font-weight: 400;
            line-height: normal;
            letter-spacing: 0.1px;
            margin-top: 42px;

            &.text-title {
                margin-bottom: 20px;

                & + .banner-text {
                    margin-top: 0;
                    padding-top: 3px;
                    max-width: 555px;
                }
            }
        }
    }
}

.custom-career-timely {
    margin-top: 55px;

    @media (max-width: 767px) {
        margin-top: 36px;
    }

    .container {
        @media (max-width: 768px) {
            padding: 0;
        }
    }
    .custom-career-timely__heading {
        color: #252525;
        font-size: 56px;
        font-style: normal;
        font-weight: 400;
        line-height: 100px; 
        letter-spacing: -0.84px;

        @media (max-width: 768px) {
            padding: 0 24px;
            font-family: "flecha-m";
            font-size: 40px;
            line-height: normal;
            letter-spacing: -0.6px;
        }
    }
    .custom-career-timely__form {
        margin-top: 21px;

        .career-timely--header {
            display: flex;
            justify-content: flex-start;
            align-items: center;
            width: 100%;
            background: #F7F3E8;
            padding: 0px 24px;
            height: 40px;
            p {
                margin-bottom: 0;
                color: #252525;
                font-size: 12px;
                font-style: normal;
                font-weight: 500;
                line-height: 18px;
                letter-spacing: 0.1px;
            }
            .header__department {
                display: block;
                width: 100%;
                max-width: 33.3%;

                @media (max-width: 768px) {
                    max-width: 53%;
                }
            }
            .header__role {
                display: block;
                width: 100%; 
                flex: 1;
                @media (max-width: 768px) {
                    max-width: 47%;
                }
            }
        }
        .career-timely--body-item {
            display: flex;
            justify-content: flex-start;
            align-items: center;
            width: 100%;
            padding: 31px 24px 48px 24px;
            border-bottom: 1px solid #F7F3E8;

            @media (max-width: 768px) {
                flex-wrap: wrap;
                padding-top: 24px;
                padding-bottom: 39px;
            }
            p {
                margin-bottom: 0px;
                font-size: 32px;
                font-style: normal;
                font-weight: 500;
                line-height: normal;
                letter-spacing: -0.48px;
                color: #252525;
                font-family: "flecha-m";

                @media (max-width: 768px) {
                    font-size: 20px;
                    font-family: "flecha-s";
                    font-weight: 500;
                    line-height: 40px; /* 200% */
                    letter-spacing: 0.1px;
                }
            }
            .body__department {
                display: block;
                width: 100%;
                max-width: 33.3%;
                @media (max-width: 768px) {
                    max-width: 53%;
                }
            }
            .body__role {
                display: block;
                width: 100%;
                max-width: 520px;
                @media (max-width: 768px) {
                    max-width: 47%;
                }
            }
            .body__action {
                display: block;
                width: 100%; 
                flex: 1;
                transform: translateY(10px);

                .button {
                    padding-left: 52px;
                    padding-right: 52px;

                    @media (max-width: 768px) {
                        padding-left: 38px;
                        padding-right: 38px;
                        font-size: 8px;
                        font-weight: 600;
                        line-height: 38px;
                        letter-spacing: 1.6px;
                        padding: 0px 36px;
                        min-width: 148px;
                    }
                }
            }   
        }
    }
}
